INSERT INTO pizzas (name, description, photo_URL, price) VALUES ('Margherita', 'La pizza classica per eccellenza, simbolo della tradizione napoletana con i suoi sapori semplici e autentici.', 'https://www.scattidigusto.it/wp-content/uploads/2018/03/pizza-margherita-originale-Scatti-di-Gusto.jpg', 6.50),('Marinara', 'Un tripudio di sapori mediterranei, con un gusto intenso grazie alla combinazione di pomodoro, aglio e origano.', 'https://www.scattidigusto.it/wp-content/uploads/2016/12/pizza-marinara-Sorbillo-768x512.jpg', 5.00),('Diavola', 'Perfetta per gli amanti dei sapori piccanti, questa pizza unisce la delicatezza della mozzarella con il carattere deciso del salame piccante.', 'https://www.negroni.com/sites/default/files/styles/scale__1440_x_1440_/public/pizza_rustica.jpg.webp?itok=yOfpO3mx', 7.50),('Quattro Formaggi', 'Un mix irresistibile di formaggi filanti che si fondono alla perfezione, creando un sapore ricco e cremoso.', 'https://thinkmilkbesmart.eu/wp-content/uploads/2024/01/pizza-quattro-formaggi.jpg', 8.00),('Prosciutto e Funghi', 'Un equilibrio perfetto tra la dolcezza del prosciutto cotto e l’intensità aromatica dei funghi.', 'https://i1.wp.com/www.piccolericette.net/piccolericette/wp-content/uploads/2019/10/4102_Pizza.jpg?fit=1216%2C616&ssl=1', 7.50),('Capricciosa', 'Una pizza ricca e gustosa che combina ingredienti saporiti per un’esperienza variegata e completa.', 'https://www.italianstylecooking.net/wp-content/uploads/2022/01/Pizza-capricciosa.jpg', 8.50),('Napoletana', 'Un classico dal sapore deciso, grazie alla presenza delle acciughe e dei capperi che donano un gusto unico.', 'https://media-cdn.tripadvisor.com/media/photo-m/1280/1d/38/4a/fe/pizza-napoli-pochi-ingredienti.jpg', 7.00),('Quattro Stagioni', 'Quattro gusti in un’unica pizza, perfetta per chi ama varietà e sapori differenti in un solo piatto.', 'https://cdn.cook.stbm.it/thumbnails/ricette/144/144880/hd750x421.jpg', 8.50),('Funghi e Salsiccia', 'Una pizza rustica e saporita che unisce la delicatezza dei funghi alla sapidità della salsiccia.', 'https://pizzeriagiacomelli.it/wp-content/uploads/2020/11/funghi_e_salsiccia-scaled.jpg', 7.80),('Vegetariana', 'Un’esplosione di colori e sapori con verdure fresche e gustose, ideale per chi cerca leggerezza e bontà.', 'https://media.istockphoto.com/id/842082336/it/foto/pizza-vegetariana-fatta-in-casa-con-peperoni-ai-funghi.jpg?s=612x612&w=0&k=20&c=fleBhQe9vDeg2qbzqFCeGpwxcWlZcA3-caABZBp2jXU=', 8.00),('Carbonara', 'L’incontro tra la tradizione della pasta e la pizza, con ingredienti saporiti che ricordano il celebre piatto romano.', 'https://www.pizzatales.it/wp50/wp-content/uploads/2021/10/la-pizza-alla-carbonara.jpg', 8.50),('Boscaiola', 'Una pizza dal gusto intenso e avvolgente, arricchita dai profumi del bosco e della salsiccia.', 'https://i0.wp.com/www.pizzeriagirasole.net/wp-content/uploads/2021/02/WhatsApp-Image-2021-02-11-at-10.28.08.jpeg?fit=1600%2C1200&ssl=1', 7.90),('Hawaiana', 'Una combinazione dolce e salata che divide gli amanti della pizza, con il contrasto unico tra ananas e prosciutto.', 'https://www.hola.com/horizon/landscape/a17cd68660e0-pizza-hawaiana-t.jpg', 7.50),('Frutti di Mare', 'Un omaggio al mare, con un mix di sapori freschi e avvolgenti che portano il profumo dell’oceano sulla pizza.', 'https://www.galbani.it/sites/default/files/styles/width_1920/public/2021-10/pizza-frutti-mare-visual.jpg?itok=8wu42Xj2', 9.00),('Tartufo', 'Un’esperienza raffinata e gourmet con il sapore inconfondibile del tartufo che avvolge ogni morso.', 'https://www.truffe-plantin.com/blog/wp-content/uploads/2021/09/summer-truffle-paste-pizza-picture-credit-la-ptite-cuisine-de-pauline-1536x1018.jpg', 10.00);
